import ProductsController from '../../../src/controllers/products';
import sinon from 'sinon';
import Product from '../../../src/models/product';

describe('Controllers: Products',()=>{
  const defaultProduct = [{
    __v: 0,
    _id: '56cb91bdc3464f14678934ca',
    name:'Default product',
    description:'product description',
    price: 100
  }];

  const defaultRequest = {
    params: { }
  }

  describe('get() products',()=>{
    it('should return a list os products', async()=>{
      const response = {
        send: sinon.spy()
      };

      Product.find = sinon.stub();

      Product.find.withArgs({}).resolves(defaultProduct);

      const productsController = new ProductsController(Product);

      await productsController.get(defaultRequest, response);

      sinon.assert.calledWith(response.send,defaultProduct);
    });
    it('should return 400 when an error occurs', async () =>{
      const request = {};
      const response = {
        send: sinon.spy(),
        status: sinon.stub()
      };

      response.status.withArgs(400).returns(response);
      Product.find = sinon.stub();
      Product.find.withArgs({}).rejects({message: 'Error'});

      const productsController = new ProductsController(Product);
      await productsController.get(request, response);

      sinon.assert.calledWith(response.send, 'Error')
    });
  });

  describe('getById()', ()=>{
    it('should return one product', async () =>{
      const fakeId = 'a-fake-id';
      const request = {
        params: {
          id: fakeId
        }
      };
      const response = {
        send: sinon.spy()
      };
      Product.find = sinon.stub();
      Product.find.withArgs({_id: fakeId}).resolves(defaultProduct);

      const productsController = new ProductsController(Product);
      await productsController.getById(request, response);

      sinon.assert.calledWith(response.send, defaultProduct);
    });
  });//describe end

  describe('create() product', () => {
    it ('should save a new product successfully', async ()=>{
      const requestWithBody = Object.assign(
        {},
        {body: defaultProduct[0]},
        defaultRequest
      );
      const response = {
        send: sinon.spy(),
        status: sinon.stub()
      };

      class fakeProduct{
        save(){}
      }

      response.status.withArgs(201).returns(response);
      sinon
        .stub(fakeProduct.prototype, 'save')
        .withArgs()
        .resolves();

      const productsController = new ProductsController(fakeProduct);

      await productsController.create(requestWithBody, response);
      sinon.assert.calledWith(response.send);
    });

    context('when an error occurs', ()=>{
      it('should return 422', async ()=>{
        const response = {
          send: sinon.spy(),
          status: sinon.stub()
        }
        class fakeProduct{
          save(){}
        }
        response.status.withArgs(422).returns(response);
        sinon
          .stub(fakeProduct.prototype, 'save')
          .withArgs()
          .rejects({message: 'Error'});

        const productsController = new ProductsController(fakeProduct);

        await productsController.create(defaultRequest, response);
        sinon.assert.calledWith(response.status, 422);
      });
    });
  });

  describe('update() product', () => {
    it('should respond with 200 when the product has been updated', async () =>{
      const fakeId = 'a-fake-id';
      const updatedProduct = {
        _id: fakeId,
        name: 'Updated product',
        description: 'Updated description',
        price: 150
      }

      const request = {
        params: {
          id : fakeId
        },
        body: updatedProduct
      };
      const response = {
        sendStatus: sinon.spy()
      };

      class fakeProduct{
        static updateOne(){}
      }

      const updateOneStub = sinon.stub(fakeProduct, 'updateOne');
      updateOneStub
        .withArgs({_id: fakeId}, updatedProduct)
        .resolves(updatedProduct);

      const productsController = new ProductsController(fakeProduct);

      await productsController.update(request, response);
      sinon.assert.calledWith(response.sendStatus, 200);
    });
    context('when an error occurs', () =>{
      it('should return 422', async() => {
        const fakeId = 'a-fake-id';
        const updatedProduct= {
          _id: fakeId,
          name:'Updated product',
          description: 'Updated description',
          price: 150
        };
        const request = {
          params: {
            id: fakeId
          },
          body: updatedProduct
        };
        const response = {
          send: sinon.spy(),
          status: sinon.stub()
        };

        class fakeProduct{
          static updateOne(){}
        }

        const updateOneStub = sinon.stub(
          fakeProduct,
          'updateOne'
        );

        updateOneStub
          .withArgs({_id: fakeId}, updatedProduct)
          .rejects({message: 'Error'});
        response.status.withArgs(422).returns(response);

        const productsController = new ProductsController(fakeProduct);

        await productsController.update(request, response);
        sinon.assert.calledWith(response.send, 'Error');

      });
    });
  });

  describe('delete() product', () => {
